INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-09-29 18:24:25', '2017-09-29 18:24:25', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'trash', 'open', 'open', '', 'hello-world__trashed', '', '', '2017-09-30 01:33:40', '2017-09-29 18:33:40', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2017-09-29 18:24:25', '2017-09-29 18:24:25', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\r\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like piña coladas. (And gettin\' caught in the rain.)</blockquote>\r\n...or something like this:\r\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\r\nAs a new WordPress user, you should go to <a href=\"http://localhost/wordpress/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'private', 'closed', 'open', '', 'sample-page', '', '', '2017-09-30 01:45:48', '2017-09-29 18:45:48', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(3, 1, '2017-09-29 18:25:14', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2017-09-29 18:25:14', '0000-00-00 00:00:00', '', 0, 'http://localhost/wordpress/?p=3', 0, 'post', '', 0),
(5, 2, '2017-09-26 16:05:00', '2017-09-26 16:05:00', 'Chúc mừng đến với WordPress. Đây là bài viết đầu tiên của bạn. Hãy chỉnh sửa hay xóa bài viết này, và bắt đầu viết blog!', 'Chào tất cả mọi người!', '', 'private', 'open', 'open', '', 'chao-moi-nguoi', '', '', '2017-09-30 17:27:32', '2017-09-30 10:27:32', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(6, 2, '2017-09-26 16:05:00', '2017-09-26 16:05:00', 'Đây là một trang mẫu. Nó khác với một bài blog bởi vì nó sẽ là một trang tĩnh và sẽ được thêm vào thanh menu của trang web của bạn (trong hầu hết theme). Mọi người thường bắt đầu bằng một trang Giới thiệu để giới thiệu bản thân đến người dùng tiềm năng. Bạn có thể viết như sau:\n<blockquote>Xin chào! Tôi là người giao thư bằng xe đạp vào ban ngày, một diễn viên đầy tham vọng vào ban đêm, và đây là trang web của tôi. Tôi sống ở Los Angeles, có một chú cho tuyệt vời tên là Jack, và tôi thích uống cocktail.</blockquote>\n...hay như thế này:\n<blockquote>Công ty XYZ Doohickey được thành lập vào năm 1971, và đã cung cấp đồ dùng chất lượng cho công chúng kể từ đó. Nằm ở thành phố Gotham, XYZ tạo việc làm cho hơn 2.000 người và làm tất cả những điều tuyệt vời cho cộng đồng Gotham.</blockquote>\nLà người dùng WordPress mới, bạn nên truy cập <a href=\"http://localhost/wordpress/wp-admin/\">trang quản trị</a> để xóa trang này và tạo các trang mới cho nội dung của bạn. Chúc vui vẻ!', 'Trang Mẫu', '', 'private', 'closed', 'open', '', 'trang-mau', '', '', '2017-09-26 16:05:00', '2017-09-26 16:05:00', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(8, 2, '2017-09-26 16:08:08', '2017-09-26 16:08:08', '<img class=\"size-medium wp-image-59 alignleft\" src=\"http://localhost/wordpress/wp-content/uploads/2017/09/13164354_1222434837767158_6489781736755951332_n-300x250.png\" alt=\"\" width=\"300\" height=\"250\" />\r\n<h2><a href=\"http://localhost/wordpress/2017/09/26/an-nhien/\">AN NHIÊN</a></h2>\r\n<div class=\"excerpt\">\r\n\r\nVị Tổng giám đốc gọi hắn lên và hỏi chuyện:- Tôi thấy cậu cũng đứng đắn, chững chạc và rạch…<a href=\"http://localhost/wordpress/2017/09/26/an-nhien/\">Xem Thêm</a><a class=\"excerpt-read-more\" title=\"Read BÍ ẨN VỀ NGÔI SAO NOEL\" href=\"http://localhost/wordpress/2017/09/26/bi-an-ve-ngoi-sao-noel/\">»</a>\r\n\r\n</div>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n<!--more-->\r\n\r\n<img class=\"size-medium wp-image-56 alignleft\" src=\"http://localhost/wordpress/wp-content/uploads/2017/09/1385341_649979305038984_778581273_n-300x267.jpg\" alt=\"\" width=\"300\" height=\"267\" />\r\n<h2><a href=\"http://localhost/wordpress/2017/09/26/bi-quan-va-lac-quan/\">BI QUAN VÀ LẠC QUAN</a></h2>\r\n<div class=\"excerpt\">\r\n\r\nMột gia đình nọ có hai người con trai sinh đôi. Chúng chỉ giống nhau ở vẻ bề ngoài còn…<a href=\"http://localhost/wordpress/2017/09/26/bi-quan-va-lac-quan/\">Xem Thêm</a><a class=\"excerpt-read-more\" title=\"Read BÍ ẨN VỀ NGÔI SAO NOEL\" href=\"http://localhost/wordpress/2017/09/26/bi-an-ve-ngoi-sao-noel/\">»</a>\r\n\r\n</div>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n<!--more-->\r\n\r\n<img class=\"size-medium wp-image-5 alignleft\" src=\"http://localhost/wordpress/wp-content/uploads/2017/09/espresso-300x180.jpg\" alt=\"\" width=\"300\" height=\"180\" />\r\n<h2><a href=\"http://localhost/wordpress/2017/09/26/danh-ngon-chiem-nghiem-cuoc-song-bai-nay-doc-hang-ngay-doc-va-lam-theo/\">DANH NGÔN &amp; CHIÊM NGHIỆM CUỘC SỐNG. Bài này đọc hàng ngày. Đọc và làm theo!</a></h2>\r\n<div class=\"excerpt\">\r\n\r\nDANH NGÔN     *Khi hướng v